mapper.select:
  		SELECT user_id,
       		   user_pwd
		FROM candle_user
		WHERE user_id=#{userId}
		AND user_pwd=#{userPwd}

select * 
from candle_user
where user_id=#{userId}
and user_pwd=#{userPwd}

mapper.insert:
    insert into candle_qna values((select nvl(max(qna_no), 0) + 1 from candle_qna),
		 #{userNo}, #{qnaTitle}, #{qnaContent}, sysdate, null, null, null )
		 #{userNo}, #{qnaTitle}, #{qnaContent}, sysdate, #{qnaReply}, #{qnaReplyDate}, #{qnaReplyUserNO} );
